import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link, useParams } from "react-router-dom";

const Edit = () => {
  const { id } = useParams();
  return (
    <>
      <div style={{ width: 500, margin: "20px auto" }}>
        <h1 className="display-1 text-center">Edit Employee {id}</h1>
        <Form.Label>First Name</Form.Label>
        <Form.Control size="lg" type="text" placeholder="First Name" />
        <br />
        <Form.Label>Last Name</Form.Label>
        <Form.Control size="lg" type="text" placeholder="Last Name" />
        <br />
        <Form.Label>Age</Form.Label>
        <Form.Control size="lg" type="text" placeholder="Age" />
        <br />
        <Form.Label>Height</Form.Label>
        <Form.Control size="lg" type="text" placeholder="in meters" />
        <br />
        <Form.Label>Gender</Form.Label>
        <Form>
          {["radio"].map((type) => (
            <div key={`inline-${type}`} className="mb-3">
              <Form.Check
                inline
                label="Male"
                name="group1"
                type={type}
                id={`inline-${type}-1`}
              />
              <Form.Check
                inline
                label="Female"
                name="group1"
                type={type}
                id={`inline-${type}-2`}
              />
            </div>
          ))}
        </Form>
        <Form.Group controlId="formFile" className="mb-3">
          <Form.Label>Attach personal Information</Form.Label>
          <Form.Control type="file" />
        </Form.Group>
        <br />
        <input type="submit" value="Update Employee" className="btn btn-dark" />
        <Link to="/" className="btn btn-danger ms-3">
          Cancel
        </Link>
      </div>
    </>
  );
};
export default Edit;
