import React, { Component, useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useSelector } from "react-redux";
import { toast } from "react-toastify";

const Add = () => {
  const [Fname, setFname] = useState("");
  const [Lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  const [salary, setSalary] = useState("");
  const [age, setAge] = useState("");
  const [height, setHeight] = useState("");
  const [gender, setGender] = useState("");
  const add = useSelector((state) => state);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!Fname || !Lname || !email || !salary || !age || !height || !gender) {
      return toast.warning("Please fill all fildes!!!!");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ width: 500, margin: "20px auto" }}>
        <h1 className="display-1 text-center">Add Employee</h1>
        <Form.Label>First Name</Form.Label>
        <Form.Control
          size="lg"
          type="text"
          placeholder="First Name"
          value={Fname}
          onChange={(e) => setFname(e.target.value)}
        />
        <br />
        <Form.Label>Last Name</Form.Label>
        <Form.Control
          size="lg"
          type="text"
          placeholder="Last Name"
          value={Lname}
          onChange={(e) => setLname(e.target.value)}
        />
        <br />
        <Form.Label>Email</Form.Label>
        <Form.Control
          size="lg"
          type="text"
          placeholder="example@example.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <br />
        <Form.Label>Salary</Form.Label>
        <Form.Control
          size="lg"
          type="text"
          placeholder="$"
          value={salary}
          onChange={(e) => setSalary(e.target.value)}
        />
        <br />
        <Form.Label>Age</Form.Label>
        <Form.Control
          size="lg"
          type="text"
          placeholder="age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
        />
        <br />
        <Form.Label>Height</Form.Label>
        <Form.Control
          size="lg"
          type="text"
          placeholder="in meters"
          value={height}
          onChange={(e) => setHeight(e.target.value)}
        />
        <br />
        <Form.Label>Gender</Form.Label>
        <Form>
          {["radio"].map((type) => (
            <div key={`inline-${type}`} className="mb-3">
              <Form.Check
                inline
                label="Male"
                name="group1"
                type={type}
                id={`inline-${type}-1`}
                value={gender}
                onChange={(e) => setGender(e.target.value)}
              />
              <Form.Check
                inline
                label="Female"
                name="group1"
                type={type}
                id={`inline-${type}-2`}
              />
            </div>
          ))}
        </Form>
        <Form.Group controlId="formFile" className="mb-3">
          <Form.Label>Attach personal Information</Form.Label>
          <Form.Control type="file" />
        </Form.Group>
        <br />
        <Button variant="primary" type="submit" onsubmit={handleSubmit}>
          Submit
        </Button>
      </div>
    </form>
  );
};
export default Add;
