import React, { Component, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";

const Edit = () => {
  const [Fname, setFname] = useState("");
  const [Lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  const [salary, setSalary] = useState("");
  const [age, setAge] = useState("");
  const [height, setHeight] = useState("");
  const [gender, setGender] = useState("");
  const { id } = useParams();

  const employes = useSelector((state) => state);
  const currentEmployee = employes.find(
    (employe) => employe.id === parseInt(id)
  );
  useEffect(() => {
    if (currentEmployee) {
      setFname(currentEmployee.firstname);
      setFname(currentEmployee.lastname);
      setFname(currentEmployee.email);
      setFname(currentEmployee.salary);
      setFname(currentEmployee.age);
      setFname(currentEmployee.height);
      setFname(currentEmployee.gender);
    }
  }, [currentEmployee]);

  return (
    <div>
      {currentEmployee ? (
        <>
          <div style={{ width: 500, margin: "20px auto" }}>
            <Form.Label>First Name</Form.Label>
            <Form.Control size="lg" type="text" placeholder="First Name" />
            <br />
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              size="lg"
              type="text"
              placeholder="Last Name"
              value={Fname}
              onChange={(e) => setFname(e.target.value)}
            />
            <br />
            <Form.Label>Email</Form.Label>
            <Form.Control
              size="lg"
              type="text"
              placeholder="example@example.com"
            />
            <br />
            <Form.Label>Salary</Form.Label>
            <Form.Control size="lg" type="text" placeholder="$" />
            <br />
            <Form.Label>Age</Form.Label>
            <Form.Control size="lg" type="text" placeholder="Age" />
            <br />
            <Form.Label>Height</Form.Label>
            <Form.Control size="lg" type="text" placeholder="in meters" />
            <br />
            <Form.Label>Gender</Form.Label>
            <Form>
              {["radio"].map((type) => (
                <div key={`inline-${type}`} className="mb-3">
                  <Form.Check
                    inline
                    label="Male"
                    name="group1"
                    type={type}
                    id={`inline-${type}-1`}
                  />
                  <Form.Check
                    inline
                    label="Female"
                    name="group1"
                    type={type}
                    id={`inline-${type}-2`}
                  />
                </div>
              ))}
            </Form>
            <Form.Group controlId="formFile" className="mb-3">
              <Form.Label>Attach personal Information</Form.Label>
              <Form.Control type="file" />
            </Form.Group>
            <br />
            <input
              type="submit"
              value="Update Employee"
              className="btn btn-dark"
            />
            <Link to="/" className="btn btn-danger ms-5">
              Cancel
            </Link>
          </div>
        </>
      ) : (
        <h1 className="display-1 text-center">
          Employee with id {id} is not exist!!{id}
        </h1>
      )}
    </div>
  );
};
export default Edit;
