import React, { Component, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";

const Home = () => {
  const employes = useSelector((state) => state);
  const dispatch = useDispatch();
  const deleteEmployee = (id) => {
    dispatch({ type: "DELETE_EMPLOYEE", payload: id });
    toast.success("Employee deleted successfully!!");
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12 my-5 ">
          <Link to="/add" className="btn btn-outline-dark text-center">
            Add Emplyee
          </Link>
        </div>
        <div className="col-md-12 mx-auto">
          <table className="table table-hover">
            <thead className="text-white bg-dark text-left">
              <tr>
                <th scope="col">Profile</th>
                <th scope="col">First Name</th>
                <th scope="col">Last Name</th>
                <th scope="col">Email</th>
                <th scope="col">Salary</th>
                <th scope="col">Age</th>
                <th scope="col">Height</th>
                <th scope="col">Gender</th>
                <th scope="col">Action</th>
              </tr>
            </thead>
            <tbody>
              {employes.map((employe, id) => (
                <tr key={id}>
                  <td>
                    <img
                      src={URL.createObjectURL(employe.profile)}
                      className="rounded-circle"
                      style={{ width: 70 }}
                      alt="Avatar"
                    />
                  </td>
                  <td>{employe.Fname}</td>
                  <td>{employe.Lname}</td>
                  <td>{employe.email}</td>
                  <td>{employe.salary}</td>
                  <td>{employe.age}</td>
                  <td>{employe.height}</td>
                  <td>{employe.gender}</td>
                  <td>
                    <Link
                      to={`/edit/${employe.id}`}
                      className="btn btn-sm btn-primary mr-2"
                    >
                      Edit
                    </Link>
                    <button
                      type="button"
                      onClick={() => deleteEmployee(employe.id)}
                      className="btn btn-sm btn-danger "
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Home;
